if(array_search($node->nodeName,$table)===false) {
  $name=$node->nodeName;
  $k=substr($name,0,1);$name=substr($name,1);
  while(isset($table[$k])) {
    $k.=substr($name,0,1);$name=substr($name,1);
  }
  $table[$k]=$node->nodeName;
}
if($node->hasAttributes()) {
  foreach($node->attributes as $a){
    if($a->name=="style")
    {
      $vars=explode(";",$a->value);
      foreach($vars as $v) if (trim($v)!="")
      {
        $sv = explode(":",$v);
        $name0 = "style.".preg_replace_callback('/\-(\w)/',array($this, "toJSAttribute"), $sv[0]);
        $name=$name0;
        $k=substr($name,0,1);$name=substr($name,1);
        while(isset($table[$k])) {
          $k.=substr($name,0,1);$name=substr($name,1);
        }
        $table[$k]=$name0;
      }
    }
    elseif($a->nodeName=="class")
    {
      $name0="className";
      $name=$name0;
      $k=substr($name,0,1);$name=substr($name,1);
      while(isset($table[$k])) {
        $k.=substr($name,0,1);$name=substr($name,1);
      }
      $table[$k]=$name0;
    }
    else
    {
      $name0=$a->nodeName;
      $name=$name0;
      $k=substr($name,0,1);$name=substr($name,1);
      while(isset($table[$k])) {
        $k.=substr($name,0,1);$name=substr($name,1);
      }
      $table[$k]=$name0;
    }
  }
}

if($node->hasChildNodes()) {
  foreach($node->childNodes as $ch){
    $this->createTable($table,$ch);
  }
}

$k=array_search($node->nodeName, $table);
$res="<".$k;
if($node->hasAttributes()) {
  foreach($node->attributes as $a){
    if($a->name=="style")
    {
      $vars=explode(";",$a->value);
      foreach($vars as $v) if (trim($v)!="")
      {
        $sv = explode(":",$v);
        $sv[0] = "style.".preg_replace_callback('/\-(\w)/',array($this, "toJSAttribute"), $sv[0]);
        $sv[0] = array_search($sv[0],$table);
        $res.= ":".$sv[0].":".str_replace(array(":","<","|",">"),array("::",":<",":|",":>"),$sv[1]);
      }
    }
    elseif($a->nodeName=="class")
    {
      $k1 = array_search("className",$table);
      $res.=":$k1:".str_replace(array(":","<","|",">"),array("::",":<",":|",":>"),$a->value);
    }
    else
    {
      $res.=":".array_search($a->nodeName,$table).":".str_replace(array(":","<","|",">"),array("::",":<",":|",":>"),$a->value);
    }
  }
}
if($node->hasChildNodes()){
  foreach($node->childNodes as $ch){
    $res.= $this->zdv($table,$ch);
  }
}
if($node->nodeType!=1)$res.=':$:'.str_replace(array(":","<","|",">"),array("::",":<",":|",":>"),$node->nodeValue);
$res.=">";

return $res;

controller.action:
try {
require_once("../library/proc_@{$name}.class.php");
$procInstance = unserialize($_SESSION["processInstance"]);
$procInstance->currentTaskInstance->savePreviousForm($_SESSION["user"],$this->getRequest()->getParams());
$formName = $procInstance->currentTaskInstance->getNextFormName($_SESSION["user"]);
if($formName) {
  $procInstance->currentTaskInstance->formDone($_SESSION["user"], $formName);
  $_SESSION["processInstance"]=serialize($procInstance);
  $this->gotoPage($formName,"index");
}
$_SESSION["processInstance"]=serialize($procInstance);
$this->gotoPage(${$name},"end");
} catch (Exception $e) {
print("<pre style='border:1px outset red;background-color:LightPink;'>".$e->getMessage()."</pre>");
}

