class DVF {
  static function getNamespace($e){
    if(!$e) return '';
    if($e->nodeName=='module') {
      return '';
    }
    if($e->nodeName=='package') {
      return   DVF::getNamespace($e->parentNode).'\\'.$e->getAttribute('name');
    }
    return DVF::getNamespace($e->parentNode);
  }
  static function getPath($e){
    if(!$e) return '';
    if($e->nodeName=='path') {
      return $e->getAttribute('path');
    }
    if($e->nodeName=='module') {
      return 'local/'.$e->getAttribute('group').'/'.$e->getAttribute('name').'/src';
    }
    if($e->nodeName=='package') {
      return DVF::getPath($e->parentNode).'/'.$e->getAttribute('name');
    }
    return DVF::getPath($e->parentNode);
  }
  static function getPackages($e){
    $ps=[];
    $packages=$e->getElementsByTagName("package");
    foreach($packages as $p){
      if($p->parentNode->parentNode===$e){
        $ps[$p->getAttribute('name')]='src';
      }
    }
    return $ps;
  }
  static function copy($source, $dest){
    if(is_dir($source)) {
        $dir_handle=opendir($source);
        while($file=readdir($dir_handle)){
            if($file!="." && $file!=".." && substr($file,0,1)!="."){
                if(is_dir($source."/".$file)){
                    if(!is_dir($dest."/".$file)){
                        mkdir($dest."/".$file);
                    }
                    DVF::copy($source."/".$file, $dest."/".$file);
                } else {
                    copy($source."/".$file, $dest."/".$file);
                }
            }
        }
        closedir($dir_handle);
    } else {
        copy($source, $dest);
    }
  }
}

class DepAdmin{
  private static $instance;
  public $composer;
  function __construct(){
    $this->composer=$this->loadComposer();
  }
  static function getInstance(){
    if(empty(DepAdmin::$instance)){
      DepAdmin::$instance=new DepAdmin();
    }
    return DepAdmin::$instance;
  }
  function loadComposer(){
    return json_decode(file_get_contents('composer.json'));
  }
  function saveComposer(){
    file_put_contents('composer.json',$this->prettify(json_encode($this->composer)));
  }
  function addRepository($name){
    $exists=false;
    if(!isset($this->composer->repositories)){
      $this->composer->repositories=[];
    }
    foreach($this->composer->repositories as $rep){
      if($rep->type=="vcs" && $rep->url=="local/$name"){
        $exists=true;
        break;
      }
    }
    if(!$exists){
      $this->composer->repositories[]=(object)[
        "type"=>"vcs",
        "url"=>"local/$name",
      ];
      $this->saveComposer();
    }
  }
  function prettify($json)
  {
    $result = '';
    $pos = 0;
    $prevChar = '';
    $outOfQuotes = true;
    $json = str_split($json);
    array_map(
        function ($char) use (&$prevChar, &$outOfQuotes, &$result, &$pos) {
        if ($char == '"' && $prevChar != '\\') {
          $outOfQuotes = !$outOfQuotes;
        } else
        {
          if (($char == '}' || $char == ']') && $outOfQuotes) {
                  $result .= PHP_EOL;
            $pos-=2;
            $result = str_pad($result, strlen($result) + $pos, " ");
          }
        }
        $result .= $char;
        if ((in_array($char, array(',', '{', '['))) && $outOfQuotes) {
          $result .= PHP_EOL;
          if (in_array($char, array('[', '{'))) {
            $pos+=2;
          }
          $result = str_pad($result, strlen($result) + $pos, " ");
        }
        $prevChar = $char;
      },
      $json
    );
    return $result;
  }
}

