interface IncludeScriptInterface {
  public function includeScript($PATH, $DATA, $CONTENT);
}

trait IncludeScriptTrait {
  public function includeScript($PATH, $DATA, $CONTENT) {
    return include($PATH);
  }
}

trait ResolveRouteTrait {
  static protected $controllerClass='\\dvf\\Controller';

  /*  |----route1----||------route2------|
   *  /route1a/route1b/route2a/route2b.php
   *  route1:
   *    class=\route1a\Route1b
   *    method=indexAction   (route2aAction no existe)
   *    script=\route1a\route1b\index.php (\route1a\route1b\route2a no existe)
   *  route2:
   *    class=\Route2a
   *    method=route2bAction
   *    script=\route2a\route2b.php
   *  @param $ROUTE = abc/def/... (No empieza con /)
   *  @param $DIR = abc/def/ (Termina con / o es vacio, no puede ser ./)
   */
  public static function resolveRoute($ROUTE, $DATA, $CONTENT=null, $DIR='') {
    if($ROUTE=='') $ROUTE='index.php';
    $aROUTE=explode("/", $ROUTE);
    $NAME=array_shift($aROUTE);
    $FILE=$DIR.$NAME;
    $CLASS='\\'.str_replace('/', '\\', $DIR).ucfirst($NAME);
    var_dump([$CLASS => class_exists($CLASS), "$FILE/"=> IS_DIR($FILE), "$FILE/-$DIR-$ROUTE"=> static::namespaceExists($FILE, $DIR, $ROUTE), REALPATH(".").":$FILE"=> FILE_EXISTS($FILE) ]);
    if(class_exists($CLASS)){
      return static::loadClass($CLASS, $FILE, $aROUTE, $DATA, $CONTENT, false);
    } elseif(static::namespaceExists($FILE, $DIR, $ROUTE)/*is_dir($FILE)*/) {
      return static::resolveRoute(implode("/", $aROUTE), $DATA, $CONTENT, $FILE.'/');
    } elseif(file_exists($FILE)) {
      return static::loadClass(static::$controllerClass, $FILE, $aROUTE, $DATA, $CONTENT, true);
    } else {
    }
  }
  protected static function loadClass($CLASS, $FILE, &$aROUTE, $DATA, $CONTENT=null, $isScript=false){
    $INSTANCE=new $CLASS();
    if(method_exists($INSTANCE, 'loadContext')) {
      $INSTANCE->loadContext();
    }
    if(count($aROUTE)>0) {
      $METHOD = explode('.',$aROUTE[0])[0].'Action';
      $SCRIPT = $FILE.'/'.$aROUTE[0];//\PagePaths::resolve($DIR, $NAME);
      if(method_exists($INSTANCE, $METHOD) || (file_exists($SCRIPT) && is_file($SCRIPT))) {
        array_shift($aROUTE);
      }elseif(file_exists($SCRIPT) && is_dir($SCRIPT)){
        array_shift($aROUTE);
        return static::resolveRoute(implode("/", $aROUTE), $DATA, $CONTENT, $SCRIPT.'/');
      } else { //if(!method_exists($INSTANCE, $METHOD) && !(file_exists($SCRIPT) && is_file($SCRIPT))) {
        $METHOD = 'indexAction';
        $SCRIPT = $FILE.'/index.php';//\PagePaths::resolve($DIR, $NAME);
      }
    } else {
      $METHOD='indexAction';
      $SCRIPT = $FILE.'/'.'index.php';//\PagePaths::resolve($DIR, $NAME);
    }
    if(count($aROUTE)>0) {
      //Start from base='' a new absolute route.
      $res0=static::resolveRoute(implode("/", $aROUTE), $DATA, $CONTENT, '');
      if(isset($res0)) $CONTENT=$res0;
    } else {
      //$CONTENT=null;
    }
    if(method_exists($INSTANCE, $METHOD)) {
      $res0 = $INSTANCE->$METHOD($DATA, $CONTENT);
      if(isset($res0)) $CONTENT=$res0;
      //$INSTANCE->$METHOD($CONTENT);
    }
    if(file_exists($SCRIPT)) {
      $res0 = $INSTANCE->includeScript($SCRIPT, $DATA, $CONTENT);
      if(isset($res0)) $CONTENT=$res0;
    }
    if($isScript) {
      $ext=explode(".",$FILE);
      $ext=$ext[count($ext)-1];
          header("Content-ext: $ext");
      switch($ext){
        case "css":
          header("Content-type: text/css");
          break;
        case "js":
          header("Content-type: text/javascript");
          break;
        case "html":
          header("Content-type: text/html");
          break;
        case "png":
          header("Content-type: image/png");
          break;
        case "php":
          break;
        default:
          $finfo = finfo_open(FILEINFO_MIME_TYPE);
          header("Content-type: ".finfo_file($finfo, $FILE));
          finfo_close($finfo);
      }
      $res0 = $INSTANCE->includeScript($FILE, $DATA, $CONTENT);
      if(isset($res0)) $CONTENT=$res0;
    }
    return $CONTENT;
  }
  static function namespaceExists($namespace, $dir, $path){
    $fpath=explode('/',$dir.$path);
    $base='\\';
    foreach($fpath as $p){
      $c=$base.ucfirst($p);
      if(class_exists($c)){
        return $c;
      }
      $base.=$p.'\\';
    }
    return false;
  }
}

class Paths {
  public static $paths=array();

  public static function add($path){
    static::$paths[]=$path;
  }

  public static function addInclude($path){
    set_include_path(get_include_path() . PATH_SEPARATOR . $path);
  }

  public static function resolve($nombre_clase, $complemento){
    $nombre_clase = str_replace('\\','/',$nombre_clase);
    if(substr($nombre_clase,0,1)!='/')$nombre_clase='/'.$nombre_clase;
    $path=false;
    foreach(static::$paths as $p){
      $P=$p.$nombre_clase . $complemento;
      if(file_exists($P)){
        $path=$P;
      }
    }
    return $path;
  }
}

