class Composer{
  private $client;
  private $currentPackages=['php'=>'5.6.5'];
  /**
   * @GET
   * @Path("/search/:search")
   */
  public function search($search){
    $this->client = new \Packagist\Api\Client();
    return [];
    $r=$this->client->search($search);
    ob_start();
    echo '<table>';
    foreach($r as $p){
      $ress=$this->evalPackage($p->getName()/*'friendsofsymfony/rest'*/);
      foreach($ress as $res){
        echo "<tr>";
        echo "<td>",$res['value'],"</td>";
        echo "<td>",$p->getFavers(),"</td>";
        echo "<td>",$p->getDownloads(),"</td>";
        echo "<td>",$res['name'],"</td>";
        echo "<td>",$p->getDescription(),"</td>";
        echo "<td>'",$res['version'],"</td>";
        echo "<td>",nl2br(print_r($res['require'],true)),"</td>";
        echo "<td>",nl2br(print_r($res['incompatible'],true)),"</td>";
        echo "</tr>";
      }
    }
    echo '</table>';
    file_put_contents('/home/david/php/analisis/rest.html', ob_get_contents() );
    ob_end_clean();
echo 'OK';
    return '';
  }
  private function evalPackage($packageName){
    $res=[];
    $versions=$this->client->get( $packageName )->getVersions();
    foreach($versions as $v){
      list($req,$incom)=$this->verify($v->getRequire());
      $value=100/(count($req)+1);
      if(count($incom)>0){
        $value-=100;
      }
      $value=count($req);
      $res[]=[
        "value"=>$value,
        "name"=>$v->getName(),
        "version"=>$v->getVersion(),
        "require"=>$req,
        "incompatible"=>$incom,
      ];
    }
    return $res;
  }
  private function verify($requires){
    $required=[];
    $incompatibles=[];
    if(!is_array($requires)){
//      var_dump("not array",$requires);
    } else 
    {
      foreach($requires as $package => $pkvs) {
        $installed=false;
        $incompatible="";
        foreach($this->currentPackages as $insPackage=>$insVersion) if($insPackage===$package){
          $installed=true;
          $insVersionNum = $this->version2num($insVersion);
          foreach(explode(',', $pkvs) as $v) {
            if(substr($v,0,2)===">="){
              $vNum=$this->version2num(substr($v,2));
              if($insVersionNum>=$vNum){
              } else {
                $incompatible="$insPackage - $insVersion";
                $installed=false;break;
              }
            } elseif(substr($v,0,2)==="<=") {
              $vNum=$this->version2num(substr($v,2));
              if($insVersionNum<=$vNum){
              } else {
                $incompatible="$insPackage - $insVersion";
                $installed=false;break;
              }
            } elseif(substr($v,0,1)===">") {
              $vNum=$this->version2num(substr($v,1));
              if($insVersionNum>$vNum){
              } else {
                $incompatible="$insPackage - $insVersion";
                $installed=false;break;
              }
            } elseif(substr($v,0,1)==="<") {
              $vNum=$this->version2num(substr($v,1));
              if($insVersionNum<$vNum){
              } else {
                $incompatible="$insPackage - $insVersion";
                $installed=false;break;
              }
            } elseif(substr($v,0,1)==="=") {
              $vNum=$this->version2num(substr($v,1));
              if($insVersionNum==$vNum){
              } else {
                $incompatible="$insPackage - $insVersion";
                $installed=false;break;
              }
            }
          }
          break;
        }
        if(!$installed){
          $required[$package] = $pkvs;
        }
        if($incompatible){
          $incompatibles[] = $incompatible;
        }
      }
    }
    return [$required,$incompatibles];
  }
  private function version2num($a){
    $a=explode('-',$a)[0];
    $a=explode('.',$a);
    $a1=0;
    foreach($a as $i=>$ai){
      $a1+=substr($ai,0,3)/pow(1000, $i);
    }
    return $a1;
  }
  /**
   * @GET
   * @Produces("text/html")
   * @Path("/lte")
   */
  public function lte(){
    require(__DIR__.'/base.php');
  }
}

