use fkooman\Http\JsonResponse;
use fkooman\Rest\Service;
use fkooman\Http\Exception\BadRequestException;

class Rest /*extends \dvf\Context*/ {
  function init($classes){
    try {
        $service = new Service();
        foreach($classes as $c) {
          $this->registerClass($service, $c);
        }
        $service->get('/rest/list', function(){
          $response = new \fkooman\Http\JsonResponse();
          $response->setContent([]);
          return $response;
        });
        $service->run()->sendResponse();
    } catch (\fkooman\Http\Exception\NotFoundException $e) {
        //
    } catch (Exception $e) {
        Service::handleException($e)->sendResponse();
    }
  }
  function registerClass($service, $c){
        $rc = new \ReflectionClass($c);
        $doc = $rc->getDocComment();
        if(preg_match('/@Path\([\'"](.+)[\'"]\)/i',$doc,$ma)) {
          $urlBase = $ma[1];
        } else {
        }
        foreach(get_class_methods($c) as $m){
          $r = new \ReflectionMethod($c, $m);
          $doc = $r->getDocComment();
          $params = $r->getParameters();
          $sparams = '';
          foreach($params as $p){
            $sparams.=',$'.$p->getName();
          }
          //@GET
          if(preg_match('/@GET/i',$doc)) $type="get";
          //@POST
          if(preg_match('/@POST/i',$doc)) $type="post";
          //@Path("/xml/:firstName")
          if(preg_match('/@Path\([\'"](.+)[\'"]\)/i',$doc,$ma)) {
            $url = $ma[1];
          } else {
            continue;
            $url = '/'.strtolower( basename(str_replace('\\','/',$c)) ).'/'.$m;
            foreach($params as $p){
              $url.='/:'.$p->getName();
            }
          }
          //@Produces("application/json")
          if(preg_match('/@Produces\([\'"](.+)[\'"]\)/i',$doc,$ma)) {
            $produces = $ma[1];
          } else {
            $produces = 'application/json';
          }
          call_user_func(array($service, $type),
              $urlBase . $url,
              eval('return function('.substr($sparams,1).') use($c,$m,$produces) {
                $o=new $c();
                $args=func_get_args();
                $res=call_user_func_array(array($o, $m), $args);
                switch($produces){
                  case "application/json":
                    $response = new \fkooman\Http\JsonResponse();
                    $response->setContent($res);
                    return $response;
                  case "text/html":
                    return "";
                }
              };')
          );
        }
  }
}

class Connection extends \PDO implements InstanceModelInterface {
  use PDOInstanceModelTrait;
}

trait PDOInstanceModelTrait{
  static $instance=null;
  public function instanceModel($class, $params=array()){
    $this->setAttribute(\PDO::ATTR_STATEMENT_CLASS, array($class, array($this)));
    $stmt=$this->prepare($class::getQuery());
    $stmt->setParams($params);
    $stmt->execute();
    return $stmt;
  }
  public function deserializeModel($serializedModel){
    $class=$serializedModel['class'];
    $this->setAttribute(\PDO::ATTR_STATEMENT_CLASS, array($class, array($this)));
    $stmt=$this->prepare($class::getQuery());
    $stmt->setParams($serializedModel['params']);
    $stmt->execute();
    return $stmt;
  }
  public static function getInstance(){
    if(!isset(static::$instance)){
      static::$instance=new static;
    }
    return static::$instance;
  }
  public static function unserializeModel($serializedModel){
    $classConn=$serializedModel['connection'];
    $conn=$classConn::getInstance();
    $class=$serializedModel['class'];
    $conn->setAttribute(\PDO::ATTR_STATEMENT_CLASS, array($class, array($conn)));
    $stmt=$conn->prepare($class::getQuery());
    $stmt->setParams($serializedModel['params']);
    $stmt->execute();
    return $stmt;
  }
}

interface InstanceModelInterface {
  public function instanceModel($class, $params=array());
}

trait FetchModelTrait {
  public function fetch($fetch_style = \PDO::FETCH_CLASS, $cursor_orientation = \PDO::FETCH_ORI_NEXT, $cursor_offset = 0 ){
    $this->setFetchMode( \PDO::FETCH_CLASS, static::getRowClass());
    $row = \PDOStatement::fetch($fetch_style, $cursor_orientation, $cursor_offset);
    if(is_object($row) && $row instanceof RowInterface) {
      $row->setModel($this);
      $row->onfetch();
    }
    return $row;
  }
  public function fetchAll($fetch_style = \PDO::FETCH_CLASS, $fetch_argument='', $ctor_args=array()) {
    $class=static::getRowClass();
    $this->setFetchMode( \PDO::FETCH_CLASS, $class);
    $rows = \PDOStatement::fetchAll($fetch_style, $class, $ctor_args);
    if(is_array($rows)) {
      foreach($rows as &$row){
        if($row instanceof RowInterface){
          $row->setModel($this);
          $row->onfetch();
        }
      }
    }
    return $rows;
  }
}

