trait SerializeModelTrait {
  protected $params=[];
  public function setParams($params){
    $this->params=$params;
    foreach($params as $a=>&$b){
      if(is_numeric($a)){
        $this->bindParam($a+1,$b);
      } else {
        $this->bindParam($a,$b);
      }
    }
  }
  public function serializeMe(){
    return ['connection'=>get_class($this->getConnection()),'class'=>get_class($this), 'params'=>$this->params];
  }
}

trait RowDMLTrait {
  public function setModel($model){
    $this->__model=$model;
    $this->__modelSerialized=$model->serializeMe();
    $me=$this;
    $this->__initialValues=new \StdClass();
    foreach($me as $k=>$v) if(substr($k,0,2)!='__'){
      $this->__initialValues->$k=$v;
    }
  }
  public function insert(){
    return $this->__model->insert($this);
  }
  public function update(){
    return $this->__model->update($this);
  }
  public function delete(){
    return $this->__model->delete($this);
  }
  //serializable
  public function __sleep()
  {
    $vars = get_object_vars($this);
    unset($vars['__model']);
    return array_keys($vars);
  }
  public function __wakeup()
  {
    $this->__model = Connection::unserializeModel($this->__modelSerialized);
  }
}

trait ModelDMLTrait {
  static $__metadata=[];
  static $__metadataTables=[];
  public function loadTableMetadata($table){
    if(isset(static::$__metadataTables[$table])){
      return static::$__metadataTables[$table];
    }
    $meta=new \StdClass();
    //COLUMNS
    $sql="SELECT 
      *
    FROM 
    information_schema.columns 
    WHERE 
      table_name = ? and
      table_schema = DATABASE()";
    $stmt=$this->dbh->prepare($sql);
    $stmt->setParams([$table]);
    $stmt->execute();
    $meta->columns=[];
    while($def=$stmt->fetch()){
      $meta->columns[$def->COLUMN_NAME]=$def;
    }
    //CONSTRAINTS
    $sql="SELECT 
     *
    FROM 
      information_schema.KEY_COLUMN_USAGE KC1
    WHERE 
      KC1.table_name = ? and
      KC1.table_schema = DATABASE()";
    $stmt=$this->dbh->prepare($sql);
    $stmt->setParams([$table]);
    $stmt->execute();
    $meta->keys=[];
    while($def=$stmt->fetch()){
      if(!isset($meta->keys[$def->CONSTRAINT_NAME])){
        $meta->keys[$def->CONSTRAINT_NAME]=new \StdClass();
        $meta->keys[$def->CONSTRAINT_NAME]->table=$table;
        $meta->keys[$def->CONSTRAINT_NAME]->name=$def->CONSTRAINT_NAME;
        $meta->keys[$def->CONSTRAINT_NAME]->columns=[];
        $meta->keys[$def->CONSTRAINT_NAME]->fnName='findBy'.static::camelize($def->CONSTRAINT_NAME);
      }
      $meta->keys[$def->CONSTRAINT_NAME]->columns[]=$def;
    }
    //FOREING KEYS LINK
    foreach($meta->keys as $k){
      foreach($k->columns as $c){
        $t=static::loadTableMetadata( $c->REFERENCED_TABLE_NAME );
        foreach($t->keys as $k2){
          $okCnt=count($k->columns);
          foreach($k2->columns as $c2){
            foreach($k->columns as $c1){
              if($c1->REFERENCED_COLUMN_NAME===$c2->COLUMN_NAME){
                $okCnt--;break;
              }
            }
          }
          if($okCnt==0){
            $k->reference=$k2;
            break;
          }
        }
      }
    }
    static::$__metadataTables[$table]=$meta;
    return $meta;
  }
  public function loadMetadata(){
    $class=get_class($this);
    if(!isset(static::$__metadata[$class])) {
      static::$__metadata[$class]=static::loadTableMetadata(static::getTable());
    }
  }
  public function createRow(){
    $class = static::getRowClass();
    $o = new $class();
    $o->setModel($this);
    return $o;
  }
  public static function getQuery(){
    return "select * from " . static::getTable();
  }
  public function insert($row){
    $class=get_class($this);
    $this->loadMetadata();
    $columns0 = array_keys(static::$__metadata[$class]->columns);
    $columns = [];
    $values = [];
    $params=[];
    foreach($columns0 as $c){
      if(!isset($row->$c)){
      } else {
        $columns[]=$c;
        $values[]="?";
        $params[]=isset($row->$c)?$row->$c:null;
      }
    }
    $sql="insert into ". static::getTable() . "(". implode(",", $columns ) .") VALUES (".implode(',',$values).")";
    $stmt=$this->dbh->prepare($sql);
    $stmt->setParams($params);
    if(!$stmt->execute()){
      $error=$stmt->errorInfo();
      if($error[0]==23000){
        //Integrity error
        preg_match('/CONSTRAINT `([^`]+)`/', $error[2], $ma);
        $constraint = $ma[1];
        if(isset(static::$__metadata[$class]->keys[$constraint]->reference)){
          throw new CodedException(static::$__metadata[$class]->keys[$constraint]->reference->table . '.' . static::$__metadata[$class]->keys[$constraint]->reference->name);
        } else {
          throw new CodedException(static::getTable() . '.' . $constraint);
        }
      } else {
        throw new \Exception('[' . $stmt->errorCode() . ']: ' . print_r($stmt->errorInfo(),true));
      }
    }
  }
  public function update($row){
    $class=get_class($this);
    $this->loadMetadata();
    $columnsValues = array_keys(static::$__metadata[$class]->columns);
    foreach($columnsValues as &$v) {$v="$v=?";}
    $params=[];
    foreach($columns as $c){
      $params[]=isset($row->$c)?$row->$c:null;
    }
    $keysValues=[];
    foreach(static::$__metadata[$class]->columns as $c=>$cc){
      if($cc->COLUMN_KEY==='PRI'){
        $keysValues[]=$c.'=?';
        $params[]=isset($row->__initialValues->$c)?$row->__initialValues->$c:null;
      }
    }
    $stmt=$this->dbh->prepare("update ". static::getTable() . " set ". implode(",", $columnsValues ) ." where " . implode(" and ", $keysValues ));
    $stmt->setParams($params);
    if(!$stmt->execute()){
      $error=$stmt->errorInfo();
      if($error[0]==23000){
        //Integrity error
        preg_match('/CONSTRAINT `([^`]+)`/', $error[2], $ma);
        $constraint = $ma[1];
        if(isset(static::$__metadata[$class]->keys[$constraint]->reference)){
          throw new CodedException(static::$__metadata[$class]->keys[$constraint]->reference);
        } else {
          throw new CodedException($constraint);
        }
      } else {
        throw new \Exception('[' . $stmt->errorCode() . ']: ' . print_r($stmt->errorInfo(),true));
      }
    }
  }
  public function delete($row){
    $class=get_class($this);
    $this->loadMetadata();
    $params=[];
    $keysValues=[];
    foreach(static::$__metadata[$class]->columns as $c=>$cc){
      if($cc->COLUMN_KEY==='PRI'){
        $keysValues[]=$c.'=?';
        $params[]=isset($row->__initialValues->$c)?$row->__initialValues->$c:null;
      }
    }
    $stmt=$this->dbh->prepare("delete from ". static::getTable() . " where " . implode(" and ", $keysValues ));
    $stmt->setParams($params);
    $stmt->execute();
    if($this->dbh->errorInfo()){
      throw new \Exception( print_r($this->dbh->errorInfo(), true) );
    }
  }
  //findBy keys function 
  static function camelize($name){
    return str_replace(' ','',ucwords( strtolower( str_replace('_',' ',$name))));
  }
  public function findBy($params){
    $class=get_class($this);
    $this->loadMetadata();
    foreach($params as $p=>$v){
      $keysValues[]="$p=:$p";
    }
    $stmt=$this->dbh->prepare("select * from ". static::getTable() . " where " . implode(" and ", $keysValues ));
    $this->setFetchMode( \PDO::FETCH_CLASS, static::getRowClass());
    $stmt->setParams($params);
    if(!$stmt->execute()){
      throw new \Exception( print_r($stmt->errorInfo(), true) );
    }
    return $stmt;
  }
  public function findByKey($keyName)
  {
    $class=get_class($this);
    $this->loadMetadata();
    $name=strtolower($keyName);
    $arguments=func_get_args();
    foreach(static::$__metadata[$class]->keys as $k=>$kk){
      //var_dump($name,$kk);
      if($name===strtolower($k) || (isset($kk->reference) && ($name===strtolower($kk->reference->name))) ){
        $params=[];
        foreach($kk->columns as $i=>$cc){
          $params[$cc->COLUMN_NAME]=$arguments[$i+1];
        }
        $stmt=$this->findBy($params);
        $row=$stmt->fetch();
        if(is_object($row) && $row instanceof RowInterface) {
          $row->setModel($this);
          $row->onfetch();
        }
        return $row;
      }
    }
  }
}

abstract class Entity extends \PDOStatement implements ModelInterface, EntityInterface {
  use ConnectionReferenceTrait, FetchModelTrait, DefaultModelsRowTrait, SerializeModelTrait, ModelDMLTrait;
}

interface EntityInterface {
  public static function getTable();
}

class Html extends \dvf\Controller {
  function indexAction($data, $content){
  }
  function stylesheetAction($data, $content){
    header('Content-type:text/css');
    echo $content;
  }
  function contentAction($data, $content){
    if(!empty($content) && is_array($content)){
      foreach($content as $k=>$node) if(is_array($node) && is_numeric($k)){
        Router::resolveRoute('html/'.strtolower($node['class']).'.php', $node, @$node['items']);
      }
    }
  }
  public static function url($base, $model, $id){
    @session_start();
    if(empty($id)) $id=uniqid('d');
    $_SESSION[$id]=$model->serializeMe();
    return Router::url($base . '?d=' . $id);
  }
  function datatableAction($data, $content){
    @session_start();
    $id=$data['d'];
    //var_dump($id,$_SESSION[$id]);
    $conn=$_SESSION[$id]['connection'];
    //TODO: $db must not be private variable (Singleton)
    $db=new $conn();
    echo json_encode($db->deserializeModel($_SESSION[$id])->fetchAll());
  }
  public static function listener($base, $callbackDI, $id){
    @session_start();
    if(empty($id)) $id=uniqid('c');
    $callback=array_pop($callbackDI);
    if($callback instanceof \Closure){
      $serializer = new \SuperClosure\Serializer();
      $serialized = $serializer->serialize($callback);
      $callbackDI[]=$serialized;
      $_SESSION[$id]=$callbackDI;
    } else {
      $callbackDI[]=$callback;
      $_SESSION[$id]=$callbackDI;
    }
    return Router::url($base . '?c=' . $id);
  }
  function listenerAction($data, $content){
    @session_start();
    $callback_id=$data['c'];
    $callbackDI=$_SESSION[$callback_id];
    $callback=array_pop($callbackDI);
    $params=[];
    foreach($callbackDI as $p){
      $params[]=@$data[$p];
    }
var_dump($callback,$params);
    if(is_string($callback) && substr($callback,0,2)=="C:"){
      $serializer = new \SuperClosure\Serializer();
      $callback = $serializer->unserialize($callback);
      call_user_func_array($callback, $params);
    } else {
      call_user_func_array($callback, $params);
//      call_user_func($callback, $data);
    }
  }
}
