path.component:
#{
/**
 * It executes the Closure Compiler (http://code.google.com/intl/es/closure/compiler/)
 * to minify the code of a "js" file.
 */

$jar = ACH_LIBRARY_PATH.'/compiler.jar';
$code = node::join("",$_);
$file = ACH_PATH.'/output/compiler.jar.xfile.js';
$errfile = ACH_PATH.'/output/compiler.jar.err';

file_put_contents($file, $code);

/* 2>&1: Add redirection so we can get stderr too. 
   note: --help is send to stderr :p
 */
$cmd='java -jar '.escapeshellarg($jar).' --js='.escapeshellarg($file).' 2>'.escapeshellarg($errfile);

exec($cmd, $output);

$err=file_get_contents($errfile);
if($err)print("\nCompilation Results:\n".$err);

return implode("",$output);
}

def.function:
//@{$name}
$_r = node::run();
$values["_"] = $_r;
$template=$this->doTemplate($this->template,$values);
//@{$preprocess}
if(isset($values["preprocess"]) && trim($values["preprocess"])!=""){
  $np=$e->ownerDocument->createElement($values["preprocess"]);
  $np->appendChild($e->ownerDocument->createTextNode($template));
  $e->appendChild($np);
  $nnp=node::factory($np);
  $template=node::join("",$nnp->run());
  $e->removeChild($np);
  unset($np);
  unset($nnp);
}
createFile($values["name"], $template);
return $this->encodeEmpty();

