  if(!empty($sort)){
    $sort = json_decode($sort);
  } else {
    $sort = array();
  }
  $binding->onread($_zdata);
  $rows = $binding->fetchAll($_zdata,$start,$limit,$page,$sort);
  $count = $binding->count($_zdata);
  if($rows) {
    echo '{data:[';
    $first=true;
    foreach($rows as $row){
      if(!$first) echo ',';
      echo json_encode(is_array($row)?$row:$row->toArray());
      $first=false;
    }
    echo '],TotalResults:'.json_encode($count).',success:true}';
  } else {
    echo '{data:[]}';
  }
} catch (Exception $ex) {
  echo $ex->getMessage();
  //echo '{data:[]}';
